svg.g:

$x = 0
for ($row=0; $row -lt 7; $row++) {
  for ($col=0; $col -lt 52; $col++) {
    $cx = $col*14
    $cy = $row*14
    $color = '#071024'
    # create a simple pattern for intensity
    if (($col + $row) % 13 -eq 0) { $color = '#06b6d4' }
    elseif (($col + $row) % 7 -eq 0) { $color = '#7c3aed' }
    elseif (($col + $row) % 5 -eq 0) { $color = '#2dd4bf' }
    "" | Out-String
  }
}
